import React, { useState } from 'react';
import Modal from 'react-modal';
import Guess from './Guess';
import './App.css';

Modal.setAppElement('#root');

function App() {
  const [guess, setGuess] = useState('');
  const [guesses, setGuesses] = useState([]);
  const [allScores, setAllScores] = useState([]);
  const [wordOfTheDay, setWordOfTheDay] = useState('مثال'); // Replace this with how you set the word of the day
  const [gameNumber, setGameNumber] = useState(208); // Example game number
  const [isCorrect, setIsCorrect] = useState(false);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Function to add a new score
  const addScore = (score) => {
    setAllScores([...allScores, score]);
  };

  const handleInputChange = (event) => {
    setGuess(event.target.value);
  };

  const handleEnterKeyPress = async (event) => {
    if (event.key === 'Enter' && guess.trim() !== '') {
      try {
        const response = await fetch('http://127.0.0.1:5000/calculate_similarity', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ word1: wordOfTheDay, word2: guess.trim() }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        const score = data.score;

        setGuesses([...guesses, { word: guess, score }]);
        addScore(score); // Assuming you have this function
        setGuess(''); // Clear input field after adding the guess

        // Check if the guess is correct
        if (guess.trim() === wordOfTheDay) {
          setIsCorrect(true);
          setModalIsOpen(true);
        }
      } catch (error) {
        console.error('Failed to fetch:', error);
      }
    }
  };

  return (
    <>
      <div className="wrapper">
        <div className="titleGame">
          <header>
            <h1>كلمتي</h1>
          </header>
          <button className="btn"><i className="fas fa-ellipsis-v"></i></button>
        </div>
        <div className="game-info">
          <span className="game-label">GAME:</span>
          <span className="game-number">#{gameNumber}</span>
          <span className="guess-label">GUESSES:</span>
          <span className="guess-number">{guesses.length}</span>
        </div>
        <div className="input-wrapper">
          <input
            type="text"
            id="guess-input"
            placeholder="Enter your guess"
            value={guess}
            onChange={handleInputChange}
            onKeyPress={handleEnterKeyPress}
          />
        </div>
        <div className="guesses" id="guess-list">
          {guesses.map((guess, index) => (
            <Guess key={index} word={guess.word} score={guess.score} allScores={allScores} />
          ))}
        </div>
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={() => setModalIsOpen(false)}
          style={{
            content: {
              top: '50%',
              left: '50%',
              right: 'auto',
              bottom: 'auto',
              marginRight: '-50%',
              transform: 'translate(-50%, -50%)',
              padding: '20px',
              textAlign: 'center',
            },
          }}
        >
          <h2>Congratulations!</h2>
          <p>You guessed the word of the day correctly!</p>
          <button onClick={() => setModalIsOpen(false)}>Close</button>
        </Modal>
      </div>
    </>
  );
}

export default App;
