import React from 'react';

function Guess({ word, score }) {
  // Calculate the width of the yellow bar based on the score
  const widthPercentage = (score / 10) * 100;

  // Calculate the RGB values for the yellow color based on the score
  const yellowColor = `rgba(255, 255, 0, 1)`;

  // Inline style for the background color of the guess
  const guessStyle = {
    background: `linear-gradient(to right, ${yellowColor} ${widthPercentage}%, transparent ${widthPercentage}% 100%)`,
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '5px', // Adjust padding as needed
    marginBottom: '5px', // Adjust margin as needed
    border: `1px solid ${score === 1 ? '#000' : 'transparent'}`, // Add border for the lowest score
    borderRadius: '3px', // Add border radius for a smoother look
  };

  return (
    <div className="guess" style={guessStyle}>
      <span className="guess-word">{word}</span>
      <span className="score">{score}</span>
    </div>
  );
}

export default Guess;
