import React, { useState } from 'react';
import Modal from 'react-modal';
import Guess from './Guess';
import './App.css';

Modal.setAppElement('#root');

function App() {
  const [guess, setGuess] = useState('');
  const [guesses, setGuesses] = useState([]);
  const [allScores, setAllScores] = useState([]);
  const [wordOfTheDay, setWordOfTheDay] = useState('سلاح'); // Replace this with how you set the word of the day
  const [gameNumber, setGameNumber] = useState(208); // Example game number
  const [isCorrect, setIsCorrect] = useState(false);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State for controlling menu visibility

  // Function to toggle menu visibility
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Function to add a new score
  const addScore = (score) => {
    setAllScores([...allScores, score]);
  };

  const handleInputChange = (event) => {
    setGuess(event.target.value);
  };

  const handleEnterKeyPress = async (event) => {
    if (event.key === 'Enter' && guess.trim() !== '') {
      // Regular expression to check for Arabic characters
      const arabicRegex = /^[\u0600-\u06FF\s]+$/;

      if (!arabicRegex.test(guess.trim())) {
        alert('Please enter a valid Arabic word.');
        return;
      }

      try {
        const response = await fetch('http://127.0.0.1:5000/calculate_similarity', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ word1: wordOfTheDay, word2: guess.trim() }),
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const data = await response.json();
        const score = data.score;

        const newGuesses = [...guesses, { word: guess, score }].sort((a, b) => a.score - b.score);
        setGuesses(newGuesses);
        addScore(score); // Assuming you have this function
        setGuess(''); // Clear input field after adding the guess

        // Check if the guess is correct
        if (guess.trim() === wordOfTheDay) {
          setIsCorrect(true);
          setModalIsOpen(true);
        }
      } catch (error) {
        console.error('Failed to fetch:', error);
      }
    }
  };

  // Function to categorize guesses by their scores
  const categorizeGuesses = () => {
    const greenGuesses = guesses.filter(g => g.score > 800).length;
    const orangeGuesses = guesses.filter(g => g.score >= 300 && g.score <= 800).length;
    const redGuesses = guesses.filter(g => g.score < 300).length;
    return { greenGuesses, orangeGuesses, redGuesses };
  };

  const { greenGuesses, orangeGuesses, redGuesses } = categorizeGuesses();

  return (
    <>
      <div className="wrapper">
        <div className="titleGame">
          <header>
            <h1>كلمتي</h1>
          </header>
          <button className="btn" onClick={toggleMenu}><i className="fas fa-ellipsis-v"></i></button>
          {isMenuOpen && (
            <div className="dropdown-menu">
              <ul>
                <li>كيف تلعب</li>
                <li>تلميح</li>
                <li>استسلام</li>
                <li>الألعاب السابقة</li>
                <li>الإعدادات</li>
                <li>اعتمادات</li>
                <li>تعليقات</li>
                <li>الأسئلة الشائعة</li>
              </ul>
            </div>
          )}
        </div>
        <div className="game-info">
          <span className="game-label">GAME:</span>
          <span className="game-number">#{gameNumber}</span>
          <span className="guess-label">GUESSES:</span>
          <span className="guess-number">{guesses.length}</span>
        </div>
        <div className="input-wrapper">
          <input
            type="text"
            id="guess-input"
            placeholder="اكتب كلمة"
            value={guess}
            onChange={handleInputChange}
            onKeyPress={handleEnterKeyPress}
          />
        </div>
        <div className="guesses" id="guess-list">
          {guesses.map((guess, index) => (
            <Guess key={index} word={guess.word} score={guess.score} allScores={allScores} />
          ))}
        </div>
        <Modal
          isOpen={modalIsOpen}
          onRequestClose={() => setModalIsOpen(false)}
          style={{
            content: {
              top: '50%',
              left: '50%',
              right: 'auto',
              bottom: 'auto',
              marginRight: '-50%',
              transform: 'translate(-50%, -50%)',
              padding: '20px',
              textAlign: 'center',
            },
          }}
        >
          <div className="modal-content">
            <h2>تهانينا!</h2>
            <p>لقد خمنت الكلمة رقم {gameNumber} بنجاح!</p>
            <p>عدد التخمينات: {guesses.length}</p>
            <div className="guesses-summary">
              <div className="guess-category">
                <span className="green-box"></span>
                <span>{greenGuesses}</span>
              </div>
              <div className="guess-category">
                <span className="orange-box"></span>
                <span>{orangeGuesses}</span>
              </div>
              <div className="guess-category">
                <span className="red-box"></span>
                <span>{redGuesses}</span>
              </div>
            </div>
            <button onClick={() => setModalIsOpen(false)} className="close-button">إغلاق</button>
          </div>
        </Modal>
      </div>
    </>
  );
}

export default App;
